import requests
import os

def download_images(image_links, output_folder):
    if not os.path.exists(output_folder):
        os.makedirs(output_folder)

    for name, link in image_links.items():
        response = requests.get(link)
        if response.status_code == 200:
            with open(os.path.join(output_folder, f"{name}.jpg"), 'wb') as f:
                f.write(response.content)
                print(f"Image saved: {name}.jpg")
        else:
            print(f"Failed to download image for {name}")


